sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var assignto = g_form.getValue('assigned_to');
	//g_form.addErrorMessage(assignto);
    var ga = new GlideAjax('GetAssignIncidentsCount');
  ga.addParam('sysparm_name','getCount');
  ga.addParam('sysparm_assigned_to',assignto);//added to pass value
  ga.getXML(callBack);

  function callBack(response) {

  var answer = response.responseXML.documentElement.getAttribute("answer");
g_form.showFieldMsg('assigned_to',"The selected assignee already has # " + answer + "  maintenance tasks assigned to them",'info');

  }

}
