var GetAssignIncidentsCount = Class.create();
GetAssignIncidentsCount.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getCount: function(){
var uid = this.getParameter('sysparm_assigned_to'); // sysparm_state should be same in both SI and Client Script. //From this you will get value from Client script
var count=0;
var gr = new GlideRecord('x_465244_kumar_ama_maintenance_tasks');
//gr.addEncodedQuery('stateNOT IN3,4,7');
//gr.addQuery('assigned_to',"800b174138d089c868d09de320f9833b");
   gr.addQuery('assigned_to', uid);
gr.query();
	//var count=0;
	while(gr.next()){

		count++;
	}
return count;

  },

    type: 'GetAssignIncidentsCount'
});
